import React from 'react';
import SkyLight from 'react-skylight';

import TableList from './common/TableList';

class CMS extends React.Component {

   constructor(props) {
      super(props);

      this.state = {
         role: this.props.location.state.loginInfoObj[0].role,
         dataList: []
      }

      this.handleLogOut = this.handleLogOut.bind(this);
      this.handleApplyModelOnClick = this.handleApplyModelOnClick.bind(this);
   }

   initDataList(loginInfoObj) {
      console.log(loginInfoObj.role);
      let url = '';

      // 'p' for Patient, 'a' for Admin
      if (loginInfoObj.role === 'p')
         url = 'http://localhost:5000/api/letters/' + loginInfoObj._id;
      else
         url = 'http://localhost:5000/api/letters/';

      fetch(url)
         .then(res => res.json())
         .then((json) => {
            console.log(json);
            this.setState({
               dataList: json
            })
         })
         .catch(err => console.log(err))
   }

   initUserRole(role) {
      if (role === 'p')
         return 'Patient';
      else
         return 'Admin'
   }

   handleApplyModelOnClick(){

   }

   handleLogOut() {
      this.props.history.push('/');
   }

   componentDidMount() {
      this.initDataList(this.props.location.state.loginInfoObj[0]);
   }

   render() {
      return (
         <div>
            <h2>{this.initUserRole(this.state.role)} CMS</h2>
            <TableList datatList={this.state.dataList} role={this.state.role} />
            <button onClick={() => this.handleApplyModelOnClick}>Apply Letter of Guarantee</button>
            <button onClick={this.handleLogOut}>Log Out</button>

            <SkyLight hideOnOverlayClicked ref={ref => this.simpleDialog = ref} title="Adim action" afterClose={this.executeAfterModalClose}>
               Do you want to {this.state.adminAction} this application?

               <form onSubmit={this.handleUpdate}>
                  <input type="text" value={this.state.remarks} onChange={this.handleRemarksChange} />
                  <input type="submit" value={this.state.adminAction} />
               </form>
               {/* <div>
                  {this.state.modelMainItem._id}
                  {this.state.modelSubItem.name}
               </div> */}
            </SkyLight>
         </div>
      )
   }
}

export default CMS;
