import React from 'react';
import SkyLight from 'react-skylight';
import './TableList.css';

class TableList extends React.Component {

   constructor(props) {
      super(props);

      this.state = {
         modelMainItem: [],
         modelSubItem: [],
         role: this.props.role
      }

      this.renderAdminBtn = this.renderAdminBtn.bind(this);
      this.renderAdminCol = this.renderAdminCol.bind(this);
      this.handleModelOnClick = this.handleModelOnClick.bind(this);
   }

   handleModelOnClick(item){
      this.setState({
         modelMainItem: item,
         modelSubItem: item.patient
      });

      this.simpleDialog.show();
      console.log(item);
   }

   renderAdminBtn(role) {
      if (role === 'a') {
         return <td>
            <button>Approve</button>
            <button>Reject</button>
         </td>
      }
   }

   renderAdminCol(role) {
      if (role === 'a') {
         return <th>
            Action
         </th>
      }
   }

   render() {
      const listItems = this.props.datatList.map(
         (item) =>
            <tr key={item._id}>
               <td><button onClick={() => this.handleModelOnClick(item)}>Open Modal</button></td>
               <td>{item.patient.name}</td>
               <td>{item.hospital}</td>
               <td>{item.date_from}</td>
               <td>{item.date_to}</td>
               <td>{item.isApproved ? 'Approves' : 'Pending'}</td>
               {this.renderAdminBtn(this.state.role)}
            </tr>
      );

      return (
         <div>
            <h2>Table List</h2>
            <table>
               <tbody>
                  <tr>
                     <th>Name</th>
                     <th>Hospital</th>
                     <th>Date From</th>
                     <th>Date To</th>
                     <th>Status</th>
                     {this.renderAdminCol(this.state.role)}
                  </tr>
                  {listItems}
               </tbody>
            </table>

            <SkyLight hideOnOverlayClicked ref={ref => this.simpleDialog = ref} title="Adim action">
               Do you want to approve this application?

               <div>
                  {this.state.modelMainItem._id}
               </div>
            </SkyLight>
         </div>
      )
   }
}

export default TableList;
