import React from 'react';

class CMS extends React.Component {

   constructor(props) {
      super(props);

      this.state = {
         dataList: []
      }
   }

   initDataList(role){
      const url = 'http://localhost:5000/api/letters/';

      fetch(url)
         .then(res => res.json())
         .then((json) => {
            console.log(json);
            this.setState({
               dataList: json
            })
         })
   }

   componentDidMount(){
      this.initDataList(role);
   }

   render() {
      return (
         <div>
            <h2>CMS</h2>
            {/* {this.state.dataList} */}
         </div>
      )
   }
}

export default CMS;
