import React from 'react';
import './TableList.css';

class TableList extends React.Component {

   constructor(props){
      super(props);

      this.state = {
         role: this.props.role
      }

      this.renderAdminBtn = this.renderAdminBtn.bind(this);
      this.renderAdminCol = this.renderAdminCol.bind(this);
   }

   renderAdminBtn(role){
      if(role === 'a') {
         return <td>
            <button>Approve</button>
         </td>
      }
   }

   renderAdminCol(role){
      if(role === 'a') {
         return <th>
            Action
         </th>
      }
   }

   render() {
      const listItems = this.props.datatList.map(
         (item) =>
            <tr key={item._id}>
               <td>{item.patient.name}</td>
               <td>{item.hospital}</td>
               <td>{item.date_from}</td>
               <td>{item.date_to}</td>
               <td>{item.isApproved ? 'Approves' : 'Pending'}</td>
               { this.renderAdminBtn(this.state.role) }
            </tr>
      );

      return (
         <div>
            <h2>Table List</h2>
            <table>
               <tbody>
                  <tr>
                     <th>Name</th>
                     <th>Hospital</th>
                     <th>Date From</th>
                     <th>Date To</th>
                     <th>Status</th>
                     { this.renderAdminCol(this.state.role) }
                  </tr>
                  {listItems}
               </tbody>
            </table>
         </div>
      )
   }
}

export default TableList;
