import React from 'react';
import SkyLight from 'react-skylight';
import moment from 'moment';

import './TableList.css';

class TableList extends React.Component {

   constructor(props) {
      super(props);

      this.state = {
         modelMainItem: [],
         modelSubItem: [],
         remarks: '',
         adminAction: '',
         isApproved: false,
         role: this.props.role
      }

      this.handleUpdate = this.handleUpdate.bind(this);
      this.renderAdminBtn = this.renderAdminBtn.bind(this);
      this.renderAdminCol = this.renderAdminCol.bind(this);
      this.handleModelOnClick = this.handleModelOnClick.bind(this);
      this.handleRemarksChange = this.handleRemarksChange.bind(this);
      this.executeAfterModalClose = this.executeAfterModalClose.bind(this);
   }

   executeAfterModalClose() {
      this.setState({
         remarks: ''
      })
   }

   handleUpdate(e) {
      e.preventDefault();
      const url = 'http://localhost:5000/api/letter/update/' + this.state.modelMainItem._id;

      fetch(url, {
         method: 'PUT',
         headers: { 'Content-Type': 'application/json' },
         body: JSON.stringify({
            isApproved: true,
            remarks: this.state.remarks
         })

      }).then(res => res.json())
         .then(res => console.log(res))
         .catch((err) => console.log(err))

      this.modelPopup.hide();
   }

   handleRemarksChange(e) {
      const re = /^[0-9\b]+$/;

      if (this.state.adminAction === 'Approve') {
         if (e.target.value === '' || re.test(e.target.value)) {
            this.setState({ remarks: e.target.value, isApproved: true });
         }
      } else {
         this.setState({ remarks: e.target.value, isApproved: false });
      }
   }

   handleModelOnClick(item, action) {
      this.setState({
         adminAction: action,
         modelMainItem: item,
         modelSubItem: item.patient
      });

      this.modelPopup.show();
   }

   renderAdminBtn(role, item) {
      if (role === 'a') {
         return <td>
            <button onClick={() => this.handleModelOnClick(item, 'Approve')}>Approve</button>
            <button onClick={() => this.handleModelOnClick(item, 'Reject')}>Reject</button>
         </td>
      } else {
         return <button onClick={() => this.handleModelOnClick(item, 'View More')}>View More</button>
      }
   }

   render() {
      const listItems = this.props.datatList.map(
         (item) =>
            <tr key={item._id}>
               <td>{item.patient.name}</td>
               <td>{item.hospital}</td>
               <td>{item.created_at}</td>
               <td>{moment(item.date_from).format('DD/MM/YYYY')} to {moment(item.date_to).format('DD/MM/YYYY')}</td>
               <td>{item.isApproved ? 'Approved' : 'Pending'}</td>
               {this.renderAdminBtn(this.state.role, item)}
            </tr>
      );

      return (
         <div>
            <h2>Table List</h2>
            <table>
               <tbody>
                  <tr>
                     <th>Patient Name</th>
                     <th>Hospital</th>
                     <th>Date Created</th>
                     <th>Expected Date Range</th>
                     <th>Status</th>
                     <th>Action</th>
                  </tr>
                  {listItems}
               </tbody>
            </table>

            <SkyLight hideOnOverlayClicked ref={ref => this.modelPopup = ref} title="Adim action" afterClose={this.executeAfterModalClose}>
               Do you want to {this.state.adminAction} this application?
               <form onSubmit={this.handleUpdate}>
                  <input type="text" value={this.state.remarks} onChange={this.handleRemarksChange} />
                  <input type="submit" value={this.state.adminAction} />
               </form>
            </SkyLight>
         </div>
      )
   }
}

export default TableList;
