import React from 'react';
import SkyLight from 'react-skylight';
import DatePicker from 'react-datepicker';
import moment from 'moment';

import TableList from './common/TableList';
import 'react-datepicker/dist/react-datepicker.css';

class CMS extends React.Component {

   constructor(props) {
      super(props);

      this.state = {
         role: this.props.location.state.loginInfoObj[0].role,
         dataList: [],
         hospital: '',
         startDate: moment()
      }

      this.handleApply = this.handleApply.bind(this);
      this.handleLogOut = this.handleLogOut.bind(this);
      this.handleDateChange = this.handleDateChange.bind(this);
      this.handleHospitalChange = this.handleHospitalChange.bind(this);
      this.handleApplyModelOnClick = this.handleApplyModelOnClick.bind(this);
   }

   initDataList(loginInfoObj) {
      console.log(loginInfoObj.role);
      let url = '';

      // 'p' for Patient, 'a' for Admin
      if (loginInfoObj.role === 'p')
         url = 'http://localhost:5000/api/letters/' + loginInfoObj._id;
      else
         url = 'http://localhost:5000/api/letters/';

      fetch(url)
         .then(res => res.json())
         .then((json) => {
            console.log(json);
            this.setState({
               dataList: json
            })
         })
         .catch(err => console.log(err))
   }

   initUserRole(role) {
      if (role === 'p')
         return 'Patient';
      else
         return 'Admin'
   }

   handleDateChange(date) {
      this.setState({
         startDate: date
      });
   }

   handleApply() {

   }

   handleApplyModelOnClick() {
      this.applyModelPopup.show();
   }

   handleLogOut() {
      this.props.history.push('/');
   }

   handleHospitalChange(e) {
      this.setState({
         hospital: e.target.value
      })
   }

   componentDidMount() {
      this.initDataList(this.props.location.state.loginInfoObj[0]);
   }

   render() {
      return (
         <div>
            <h2>{this.initUserRole(this.state.role)} CMS</h2>
            <TableList datatList={this.state.dataList} role={this.state.role} />
            <button onClick={this.handleApplyModelOnClick}>Apply Letter of Guarantee</button>
            <button onClick={this.handleLogOut}>Log Out</button>

            <SkyLight hideOnOverlayClicked ref={ref => this.applyModelPopup = ref} title="Letter of Guarantee" afterClose={this.executeAfterModalClose}>
               Apply Letter of Guarantee
               <form onSubmit={this.handleApply}>
                  <select name="hospital" value={this.state.hospital} onChange={this.handleHospitalChange}>
                     <option value="hospitalA">Hospital A</option>
                     <option value="hospitalB">Hospital B</option>
                     <option value="hospitalC">Hospital C</option>
                  </select>

                  <DatePicker
                     selected={this.state.startDate}
                     onChange={this.handleDateChange} />

                  <input type="text" value={this.state.remarks} onChange={this.handleRemarksChange} />
                  <input type="submit" value={this.state.adminAction} />
               </form>
            </SkyLight>
         </div>
      )
   }
}

export default CMS;
